import React, { useState, useEffect } from "react";
import Navbar from "./sub-components/Navbar";
import Web3 from "web3";

// import { loadavg } from "os";

const App = () => {
  const [account, setAccount] = useState("0x0");
  const [web3Api, setWeb3Api] = useState({
    provider: null,
    web3: null,
  });

  useEffect(() => {
    //This loadProvider method will detect metaMask and if present will connect the app to it.
    // Otherwise it will raise an alert/warning to the user to download metaMask.
    const loadProvider = async () => {
      let provider = null;
      if (window.ethereum) {
        provider = window.ethereum;
        try {
          await provider.enable();
        } catch (error) {
          console.log("User Not Allowed.");
        }
      } else if (window.web3) {
        provider = await window.web3.currentProvider;
      } else if (!process.env.production) {
        provider = await new Web3.providers.HttpProvider("http://localhost:7545");
      } else {
        window.alert("Download MetaMask to run this application!");
      }
      setWeb3Api({
        web3: new Web3(provider),
        provider,
      });
    };

    // const currentAccount = async () => {
    //   const web3 = window.web3;
    //   const account = await web3.eth.getAccounts();
    //   console.log(account);
    // };

    // const loadProvider = async () => {
    //   await console.log(window.web3);
    //   await console.log(window.ethereum);

    //   // console.log("Metamask not detected.");
    // };
    loadProvider();
    // currentAccount();
  }, []);
  console.log(web3Api.web3);
  return (
    <>
      <Navbar account={account} />
    </>
  );
};

export default App;
