import React, { useState, useEffect } from "react";
import Navbar from "./sub-components/Navbar";
import Web3 from "web3";
import Tether from "../truffle.abis/Tether.json";
import detectEthereumProvider from "@metamask/detect-provider";

const App = () => {
  const [account, setAccount] = useState(null);
  const [web3Api, setWeb3Api] = useState({
    provider: null,
    web3: null,
  });
  // const [networkId, setNetworkId] = useState(null);
  const [tether, setTether] = useState({});
  // const [rwd, setRwd] = useState({});
  // const [decentralBank, setDecentralBank] = useState({});
  // const [stakingBalance, setStakingBalance] = useState(0);
  const [tetherBalance, setTetherBalance] = useState(0);
  // const [rwdBalance, setRwdBalance] = useState(0);
  // const [loading, setLoading] = useState(true);

  useEffect(() => {
    //This loadProvider method will detect metaMask and if present will connect the app to it.
    // Otherwise it will raise an alert/warning to the user to download metaMask.
    const loadProvider = async () => {
      //   // method one to detect metaMask
      //   let provider = null;
      //   if (window.ethereum) {
      //     provider = window.ethereum;
      //     try {
      //       await provider.enable();
      //     } catch (error) {
      //       console.error("User Not Allowed.");
      //     }
      //   } else if (window.web3) {
      //     provider = await window.web3.currentProvider;
      //   } else if (!process.env.production) {
      //     provider = await new Web3.providers.HttpProvider(
      //       "http://localhost:7545"
      //     );
      //   } else {
      //     window.alert("Download MetaMask to run this application!");
      //   }

      // method two to detect metamask with the the help of npm package
      const provider = await detectEthereumProvider();
      try {
        provider.request({ method: "eth_requestAccounts" });
        setWeb3Api({
          web3: new Web3(provider),
          provider,
        });
      } catch (error) {
        console.error("Please install MetaMask.");
      }
    };
    loadProvider();
  }, []);
  // console.log(web3Api.web3);

  useEffect(() => {
    // getting the current account
    const currentAccount = async () => {
      const accounts = await web3Api.web3.eth.getAccounts();
      setAccount(accounts[0]);
    };

    web3Api.web3 && currentAccount();
  }, [web3Api.web3]);
  // console.log(web3Api.web3);
  // console.log(account);

  useEffect(() => {
    //   // loading contract data from blockChain
    const loadContract = async () => {
      // GET network id of the contract
      const network_Id = await web3Api.web3.eth.net.getId();
      // setNetworkId(network_Id );
      console.log(network_Id, "netId");
      const tetherData = await Tether.networks[network_Id];
      // console.log(tetherData);

      if (tetherData) {
        const _tether = new web3Api.web3.eth.Contract(
          Tether.abi,
          tetherData.address
        );
        // console.log(tetherContract);
        setTether({ tether: _tether });
        console.log(tether);

        // let _tetherBalance = await tetherContract.methods
        //   .balanceOf(account)
        //   .call();
        // setTetherBalance(_tetherBalance.toString());
      } else {
        window.alert(`Error! no network detected. Contract not deployed.`);
      }
    };

    web3Api.web3 && loadContract();
    // console.log({ tether });
    // console.log(tetherData.address);
    //   // console.log({ balance: tetherBalance });
  }, [web3Api.web3]);

  return (
    <>
      <Navbar account={account} />
    </>
  );
};

export default App;
